// SWIPE FEATURE
var xDown = null;
var yDown = null;

function add_swipee(element) {
    element.addEventListener('touchstart', handleTouchStartCol, {passive: true});
    element.addEventListener('touchmove', handleTouchMoveCol, {passive: true});
}
function handleTouchStartCol(evt) {
    xDown = evt.touches[0].clientX;
    yDown = evt.touches[0].clientY;
};

function handleTouchMoveCol(evt) {
    if (!xDown || !yDown) {
        return;
    }
    var xUp = evt.touches[0].clientX;
    var yUp = evt.touches[0].clientY;
    var xDiff = xDown - xUp;
    var yDiff = yDown - yUp;
    if (Math.abs(xDiff) + Math.abs(yDiff) > 150) { //to deal with to short swipes
        if (Math.abs(xDiff) > Math.abs(yDiff)) { /*most significant*/
            if (xDiff > 0) {
                var prod_card=evt.target.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode;
                slideToCollection(null,0,prod_card,"left")
            } else { /* right swipe */
                var prod_card=evt.target.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode;
                slideToCollection(null,0,prod_card,"right")
            }
        }
        /* reset values */
        xDown = null;
        yDown = null;
    }
    if (Math.abs(xDiff) + Math.abs(yDiff) < 150) {
        if (Math.abs(xDiff) > Math.abs(yDiff)) { /*most significant*/
            if (xDiff > 0) { /* left swipe */
                var prod_card=evt.target.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode;
                slideToCollection(null,0,prod_card,"left")
            } else { /* right swipe */
                var prod_card=evt.target.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode.parentNode;
                slideToCollection(null,0,prod_card,"right")
            }
        }
        /* reset values */
        xDown = null;
        yDown = null;
    }
}
