function review_pagination_clicks(){
  var nextpagebtn = document.querySelector('.pr-rd-pagination-btn.pr-rd-pagination-btn--next')
  if(nextpagebtn != null){
    nextpagebtn.addEventListener('click',function(e){
      e.preventDefault()
      inc_col_scroll_pageh = document.documentElement.scrollTop || document.body.scrollTop
      var curpage=this.getAttribute('currentpage')
      var prevpagecheck = parseInt(curpage) + (1)
      for(var pg=0;pg<INC_COL.config.incpagination.length;pg++){
          if(parseInt(INC_COL.config.incpagination[pg].currentPage) == parseInt(prevpagecheck)){
              var reviewDisp = generate_html_tag('div','pr-review-display')
              if(document.querySelector('.pdp-mobile') != null || window.innerWidth < 821){
                reviewDisp.classList.add('pr-snapshot-mobile')
              }else{
                reviewDisp.classList.add('pr-snapshot-desktop')
              }
              document.querySelector('#pr-review-display').innerHTML = INC_COL.config.incpagination[pg].html
              review_pagination_clicks()
              setTimeout(function(){
                document.getElementById("pr-review-display").scrollIntoView()
              },200)
          }
      }
    })
  }
  var prevpagebtn = document.querySelector('.pr-rd-pagination-btn.pr-rd-pagination-btn--previous')
  if(prevpagebtn != null){
    prevpagebtn.addEventListener('click',function(e){
      e.preventDefault()
      inc_col_scroll_pageh = document.documentElement.scrollTop || document.body.scrollTop
      var curpage=this.getAttribute('currentpage')
      var prevpagecheck = parseInt(curpage) - (1)
      for(var pg=0;pg<INC_COL.config.incpagination.length;pg++){
        if(parseInt(INC_COL.config.incpagination[pg].currentPage) == parseInt(prevpagecheck)){
            var reviewDisp = generate_html_tag('div','pr-review-display')
            if(document.querySelector('.pdp-mobile') != null || window.innerWidth < 821){
              reviewDisp.classList.add('pr-snapshot-mobile')
            }else{
              reviewDisp.classList.add('pr-snapshot-desktop')
            }
            document.querySelector('#pr-review-display').innerHTML = INC_COL.config.incpagination[pg].html
            review_pagination_clicks()
            setTimeout(function(){
              document.getElementById("pr-review-display").scrollIntoView()
            },200)
        }
      }
    })
  }
  var elmcondensed = document.querySelectorAll('.inc_powerrating .pr-review.pr-review-condensed')
  for(var rv=0;rv<elmcondensed.length;rv++){
    if(elmcondensed[rv].querySelector('.pr-rd-show-more-description-btn') != null){
      elmcondensed[rv].querySelector('.pr-rd-show-more-description-btn').addEventListener('click',function(e){
        if(this.parentNode.querySelector('.comment_text').classList.contains('more_') == true){
          var more_text = this.parentNode.querySelector('.comment_text').getAttribute('comment_more')
          this.parentNode.querySelector('.comment_text').innerHTML = more_text
          this.parentNode.querySelector('.comment_text').classList.remove('more_')
          this.parentNode.querySelector('.comment_text').classList.add('less_')
          this.innerHTML = "[ - ]"
        }else{
          var more_text = this.parentNode.querySelector('.comment_text').getAttribute('comment_short')
          this.parentNode.querySelector('.comment_text').innerHTML = more_text + "..."
          this.parentNode.querySelector('.comment_text').classList.remove('less_')
          this.parentNode.querySelector('.comment_text').classList.add('more_')
          this.innerHTML = "[ + ]"
        }
      })
    }
    elmcondensed[rv].querySelector('.pr-accordion-btn').addEventListener('click',function(e){
      if(e.currentTarget.parentNode.classList.contains('pr-accordion-collapsed') == true){
        e.currentTarget.parentNode.classList.remove('pr-accordion-collapsed')
        e.currentTarget.parentNode.classList.add('pr-accordion-exapanded')
        this.querySelector('span').innerText = 'Less Details'
      }else{
        e.currentTarget.parentNode.classList.remove('pr-accordion-exapanded')
        e.currentTarget.parentNode.classList.add('pr-accordion-collapsed')
        this.querySelector('span').innerText = 'More Details'
      }
    })
    //ugc_id
    elmcondensed[rv].querySelector('.pr-rd-flag-review-btn').addEventListener('click',function(e){
      var elmugcid = this.getAttribute('ugcid')
      load_flag_review_modal(this,elmugcid)
    })
    elmcondensed[rv].querySelector('.pr-helpful-btn.pr-helpful-yes').addEventListener('click',function(e){
      var ugc_id = this.getAttribute('ugcid')
      var elm = this.parentNode
      helpful_unhelpful_('helpful',ugc_id,elm)
    })
    elmcondensed[rv].querySelector('.pr-helpful-btn.pr-helpful-no').addEventListener('click',function(e){
      var ugc_id = this.getAttribute('ugcid')
      var elm = this.parentNode
      helpful_unhelpful_('Nohelpful',ugc_id,elm)
    })
  }
}

